define({
    inputs: {
        scrollView: "reference",
        tabBar: "reference",
        pageCount: "number",
    },
    outputs: {
    },
    setup: function(inputs, outputs) {
        var _this = this;
        this.createTabs = function() {
            while(inputs.tabBar._internal.children.length > 0) {
                inputs.tabBar.removeChild(inputs.tabBar._internal.children[0]);
            }

            var pages = inputs.scrollView.getChildren();
            for(var i = 0; i < pages.length; i++) {
                var page = pages[i];
                var title = page._internal.root._cachedInputValues.title;
                var tab = Noodl.createComponent('UI Kit/Navigation/Internal/Tab Icon');
                tab.setInputValue('Label',title);
                inputs.tabBar.addChild(tab);
            }

            _this.lastPageCount = inputs.pageCount; 
            _this.createTabsScheduled = false;
        }
        this.scheduleCreateTabs = function() {
            if(!_this.createTabsScheduled) {
                setTimeout(_this.createTabs.bind(_this),0);
                _this.createTabsScheduled = true;
            }
        }
    },
    run: function(inputs, outputs) {
        if(inputs.pageCount && inputs.pageCount !== this.lastPageCount) {
            this.scheduleCreateTabs();
        }
    }
});

define({
    inputs: {
        state: "boolean",
        toggle: "boolean",
    },
    outputs: {
        state: "boolean",
        toggled: "boolean"
    },
    setup: function(inputs, outputs) {
        outputs.state = this.state = false;
    },
    run: function(inputs, outputs) {
        if(this.lastInputState !== inputs.state) {
            // Input state has changed
            var newState =  (!!inputs.state);

            if(this.state !== newState) {
                this.state = newState;
                outputs.state = this.state;
                this.flagOutputDirty('state');
            }

            this.lastInputState = inputs.state;
        }

        if(inputs.toggle) {
            // Toggle from user input
            this.state = !this.state;
            outputs.state = this.state;
            this.flagOutputDirty('state');
            this.sendSignalOnOutput('toggled');
        }
    }
});
